import React from 'react';
import HeaderComponent from "../components/Header/header";
import FooterComponent from "../components/Footer/footer";
import SearchComponent from "../components/Search/search";
import axios from "axios";
import RealDataBase from "../RealDataBase";
let shelterZipCodeArray = [];
class Home extends React.Component {

  //ToDo add constructor, state, component did mount and axios function to store shelters in state.
  constructor(props) {
    super(props);
    this.state = {
      dogs:[],
      zipcodes:[]
    };
  }

  componentDidMount() {
    axios.get('/api/dogs/').then(res => {
      this.setState({ dogs: res.data })
      console.log(this.state.dogs);
      storeZipCodes()
    });
    function storeZipCodes() {
      let zipcodeArray = [];
      for (var i = 0; i < this.state.dogs.length; i++) {
        zipcodeArray.push(this.state.dogs[i].shelterZipcode)
      }
      this.setState({ zipcodes: zipcodeArray })

      console.log(this.state.zipcodes)
    }
  };


  render() {
    return (
      <div className="home-page">
        <div className="myHeader">
          <HeaderComponent></HeaderComponent>
        </div>
        <div className="mySearch">
          <SearchComponent suggestions={shelterZipCodeArray} ></SearchComponent>
        </div>
        <div className="myFooter">
          <FooterComponent></FooterComponent>
        </div>
      </div>
    );
  }
}

export default Home;
